import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      {/* Mobile hamburger button */}
      <button 
        className="navbar-toggler d-md-none position-fixed" 
        style={{
          top: '10px',
          left: '10px',
          zIndex: 1050,
          backgroundColor: 'rgba(255,255,255,0.7)',
          border: '1px solid #dee2e6'
        }}
        onClick={toggleNavbar}
        aria-expanded={isOpen}
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>

      {/* Vertical navbar - mobile */}
      <nav 
        className={`d-flex flex-column p-3 bg-light ${isOpen ? '' : 'd-md-none collapse'}`}
        style={{
          width: '250px',
          height: '100vh',
          position: 'fixed',
          left: 0,
          top: 0,
          zIndex: 1040,
          transform: isOpen ? 'translateX(0)' : 'translateX(-100%)',
          transition: 'transform 0.3s ease'
        }}
      >
        <Link 
          className="navbar-brand mb-4" 
          to="/"
          onClick={() => setIsOpen(false)}
        >
          Your Brand
        </Link>

        <div className="d-flex flex-column">
          <Link 
            className="nav-link py-2" 
            to="/" 
            onClick={() => setIsOpen(false)}
          >
            Home
          </Link>
          <Link 
            className="nav-link py-2" 
            to="/add-reservation" 
            onClick={() => setIsOpen(false)}
          >
            Add Reservation
          </Link>
          <Link 
            className="nav-link py-2" 
            to="/account" 
            onClick={() => setIsOpen(false)}
          >
            Account
          </Link>
          <Link 
            className="nav-link py-2" 
            to="/settings" 
            onClick={() => setIsOpen(false)}
          >
            Settings
          </Link>
          <Link 
            className="nav-link py-2 text-danger mt-3" 
            to="/logout" 
            onClick={() => setIsOpen(false)}
          >
            Log Out
          </Link>
        </div>
      </nav>

      {/* Horizontal navbar - desktop */}
      <nav className="navbar navbar-expand-md navbar-light bg-light d-none d-md-flex">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">Your Brand</Link>
          <div className="d-flex gap-3">
            <Link className="nav-link" to="/">Home</Link>
            <Link className="nav-link" to="/add-reservation">Add Reservation</Link>
            <Link className="nav-link" to="/account">Account</Link>
            <Link className="nav-link" to="/settings">Settings</Link>
            <Link className="nav-link text-danger" to="/logout">Log Out</Link>
          </div>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
